<script>
import { slider, slideritem } from 'vue-concise-slider'
import Carousel from "@/components/Carousel";
import Card from "@/components/Card";
import CardProduk from "@/components/CardProduk";
// import DialogLogout from "@/components/DialogLogout";
import { LOGOUT } from "@/store/actions.type";
import axios from "axios";

export default {
  name: 'Home',
  components: {
    Carousel,
    Card,
    slider,
    slideritem,
    CardProduk
    // DialogLogout
  },
  data () {
    return {
      loading: true,
      dialogLogout: false,
      ternaks: [],
      produks: [],
      profile:[],
      //Slider configuration [obj]
      options: {
        pagination: false,
        thresholdDistance: 100, // Sliding distance threshold
        thresholdTime: 300, // Sliding time threshold decision
        grabCursor: true, // Scratch style
        speed: 300, // Sliding speed
        infinite: 2,
        slidesToScroll: 2,
        // timingFunction: 'ease', // Sliding mode
        // loop: false, // Infinite loop
        // autoplay: 0 // Auto play[ms]
      },
      search:{},
      city: [],
      city_produk: [],
      mandatory: false,
      xs: false,
      sm: false,
    }
  },
  methods: {
    setternaks(data) {
      this.ternaks = data;
    },
    setProduks(data) {
      this.produks = data;
    },
    setCity(data) {
      this.city = data;
    },
    setCityProduk(data) {
      this.city_produk = data;
    },
    setProfile(data) {
      this.profile = data;
    },
    logout() {
      this.dialogLogout = true
    },
    closeLogout () {
      this.dialogLogout = false
    },
    logoutItemConfirm () {
        this.$store.dispatch(LOGOUT).then(() => {
          this.$router.push({ name: "login" });
      });
    },
    searchTernak(city_id) {
        this.search.city_id = city_id
        axios
        // .post("cariternak/?nama="+this.search.nama+"&ukuran="+this.search.ukuran)
        .post("cariternak",this.search)
        .then((response) => this.setternaks(response.data.ternak.filter(ternak => {
                return ternak.ternak_st == '1' && ternak.order_id == null
            })))
        .catch((error) => console.log(error))
    },
    searchProduk(city_id) {
        this.search.city_id = city_id
        axios
        .post("cariproduk",this.search)
        .then((response) => {
            console.log(response)
            this.setProduks(response.data.data)
        })
        .catch((error) => console.log(error))
    },
  },
  beforeMount() {
    this.setProfile(this.$store.state.auth.user)
  },
  mounted() {
    this.xs = this.$vuetify.breakpoint.xs
    this.sm = this.$vuetify.breakpoint.sm
    if (!this.xs){
      this.options.infinite = 3
      this.options.slidesToScroll = 3
    }
    axios
      .get("ternak")
      .then((response) => 
        {
          this.loading = false
          this.setternaks(response.data.ternak.filter(ternak => {
              return ternak.ternak_st == '1' && ternak.order_id == null
          }))
        }
        )
      .catch((error) => console.log(error))
    axios
      .get("lokasi/kota_aktif")
      .then((response) => this.setCity(response.data.kota))
      .catch((error) => console.log(error))
    axios
      .get("produk")
      .then((response) => 
          {
              this.loading = false
              this.setProduks(response.data.data)  
          }
        )
      .catch((error) => console.log(error))
    axios
      .get("lokasi/kota_aktif_produk")
      .then((response) => this.setCityProduk(response.data.kota))
      .catch((error) => console.log(error))
  },
  inject: {
      theme: {
        default: { isDark: false },
      },
    },
}
</script>
